import React, { useState } from 'react'
import Navbar from './Navbar'
import { useEffect } from 'react'
import { useLocation } from "react-router-dom"
import axios from 'axios'
import $ from 'jquery'

const Index = () => {
  const [pic, setpic] = useState("default.png");
  const [islog, setislog] = useState(false);
  const [isplay, setisplay] = useState(false);
  const [uname, setuname] = useState("");
  const [mode, setmode] = useState("light");
  const [songs, setsongs] = useState([]);
  useEffect(() => {
    axios.get("http://localhost:8000/getSong").then((res) => {

      setsongs(res.data);
    }).catch((err) => {
      console.log(err);
    })

    if (sessionStorage.getItem("users") != null) {
      const user = sessionStorage.getItem("users");
      const users = JSON.parse(user)
      const pics = `http://localhost:8000/users/${users.pic}`
      setpic(pics);
      setuname(users.uname)
      setislog(true)
    }
    const md = sessionStorage.getItem('theme')
    document.body.className = md
  }, [mode, songs])
  const logout = () => {
    sessionStorage.removeItem("users");
    setislog(false);
    setpic("default.png")
  }
  const mod = () => {
    if (mode === "light") {
      setmode("dark");
      sessionStorage.setItem("theme", "dark");
    }
    else {
      setmode("light");
      sessionStorage.setItem("theme", "light");
    }

  }

  return (
    <>
      <div className='container-fluid'>
        <div className='row'>
          <div className='col-md-2 '>
          <nav class="navbar navbar-expand-lg navbar-light bg-light">
  <div class="container-fluid">
    <a class="navbar-brand" href="#">Navbar</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarNav">
      <ul class="navbar-nav">
        <li class="nav-item">
          <a class="nav-link active" aria-current="page" href="#">Home</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#">Features</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#">Pricing</a>
        </li>
        <li class="nav-item">
          <a class="nav-link disabled" href="#" tabindex="-1" aria-disabled="true">Disabled</a>
        </li>
      </ul>
    </div>
  </div>
</nav>
          </div>
          {/* <div className='col-md-2 leftnav'>
            <div className='text-center mt-4'>
              <img src={pic} className='userprf my-2'></img>
              <br></br>
              {islog ?
                <a style={{ "textDecoration": "none", "fontWeight": "bold", "cursor": "pointer" }} >{uname}</a>
                :
                <a href='login' style={{ "textDecoration": "none", "fontWeight": "bold" }} >LOGIN</a>
              }
            </div>

            <div className='navIteam'>
              <div>
                <ul>
                  <li ><i className="fa fa-moon-o"></i>&nbsp;&nbsp;<a onClick={mod}>Mode</a></li>
                  <li><i className="fa fa-home" aria-hidden="true"></i>&nbsp;&nbsp;<a>Home</a></li>
                  <li><i className="fa fa-history" aria-hidden="true"></i>&nbsp;&nbsp;<a>Recent</a></li>
                  <li><i className="fa fa-list" aria-hidden="true"></i>&nbsp;&nbsp;<a>Playlist</a></li>
                  <li><i className="fa fa-download" aria-hidden="true">&nbsp;&nbsp;</i><a>Download</a></li>
                  {islog ?
                    <li ><i className="fa fa-sign-out" aria-hidden="true">&nbsp;</i><a onClick={logout} >LOGOUT</a></li>
                    :
                    ""
                  }
                </ul>
              </div>
            </div>
          </div> */}
          <div className='col-md-10 rightnav'>
            <Navbar></Navbar>
            <div className='rightimg'>
              <img src='rightback.jpg' className=''></img>
            </div>
            <div className='container-fluid'>
              <div className='mt-3 my-3 row row-cols-1 row-cols-md-5 g-4'>
                {
                  songs.map((data) => {
                    let url = `http://localhost:8000/getSong/${data.SongImg}`
                    let song = `http://localhost:8000/getSong/${data.Song}`
                    return (
                      <div className='col' key={data._id}>
                        <div className='card text-center xyz'>
                          <div>
                            <img src={url} className="sogimg"></img>
                          </div>
                          <div className='playicon'>
                            <i className="fa fa-play"  aria-hidden="true" onClick={(ev) => {
                              let sog = document.getElementById('sog');

                              $('.playicon i').removeClass("fa fa-pause")
                              $('.playicon i').addClass("fa fa-play")
                              sog.src = song;
                              if (isplay) {
                                sog.pause();
                                setisplay(false);
                                ev.target.className="fa fa-play";
                              }
                              else {
                                sog.play();
                                setisplay(true);
                                ev.target.className="fa fa-pause";
                              }


                            }}></i>
                          </div>
                          <h6 className='mt-2'>{data.Name}</h6>
                        </div>
                      </div>
                    )
                  })
                }
                <audio id="sog" onEnded={()=>{
                  $('.playicon i').removeClass("fa fa-pause")
                  $('.playicon i').addClass("fa fa-play")
                }}></audio>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default Index
