import React,{useState,useEffect} from 'react'
import {useParams} from "react-router-dom"
import $ from "jquery"
import axios from 'axios'

const PlaySong = () => {
  const params=useParams();
  const [isplay, setisplay] = useState(false);
  const [songs, setsongs] = useState([]);
  useEffect(() => {
    const md = sessionStorage.getItem('theme')
    document.body.className = md;
    axios.get("http://localhost:8000/getSong").then((res) => {

      setsongs(res.data);
  }).catch((err) => {
      console.log(err);
  })

}, [])

  return (
    <>
      <div className='container playbox  mt-5 shadow-lg p-3 mb-5 rounded'>
        <div className='row'>
            <div className='col-md-4 playleft'>
             <div className='mt-5'>
             <img src={'http://localhost:8000/getSong/'+params.songimg} className="playimg"></img>
             </div>
            </div>
            <div className='col-md-8 playright'>
              <div className='mt-5'>
              <div className='row'>
              <div className='sogdetail'>
               <h5>{params.name}</h5>
              </div>
              </div>
              <div className='row'>
              <div className='sogslider'>
                <div><input type="range" className="slider"></input></div>
                <div className='sogicondiv'>
                <div className='sogIcon'><i className='fa fa-backward'></i></div>
                <div className='sogIcon'><i className='fa fa-play-circle' onClick={(ev)=>{
                  let sog=document.getElementById('sog');
                  sog.src="http://localhost:8000/getSong/"+params.song;
                  if (isplay) {
                    sog.pause();
                    setisplay(false);
                    ev.target.className = "fa fa-play";
                }
                else {
                    sog.play();
                    setisplay(true);
                    ev.target.className = "fa fa-pause";
                }
                }}></i></div>
                <div className='sogIcon'><i className='fa fa-forward'></i></div>
                </div>
              </div>
              </div>
              </div>
            </div>
        </div>
      </div>
      <div className='songsuggestion'>

       {
         songs.map((data)=>{
           let simg=`http://localhost:8000/getSong/${data.SongImg}`
           return(
             <div className='card'>
               <div className='card-body'>
                 <div className='row'>
                   <div className="col-md-3">
                     <img src=""></img>
                   </div>
                   <div className="col-md-9"></div>
                 </div>
               </div>
             </div>
           )
         })
       }

      </div>
      <audio id='sog' src=''></audio>
    </>
  )
}

export default PlaySong
