import React, { useState } from 'react'
import axios from 'axios'

const NewUser = () => {
    const [User, setUser]=useState({
        name:"",email:"",uname:"",pass:""
    });
    const [Pic, setPic]=useState();

const getData=(ev)=>{
  let name=ev.target.name;
  let value=ev.target.value;

  setUser({...User,[name]:value})
}
const sub=(ev)=>{
ev.preventDefault();

let form=new FormData();
form.append("name",User.name);
form.append("email",User.email);
form.append("uname",User.uname);
form.append("pass",User.pass);
form.append("Pic",Pic);

axios.post("http://localhost:8000/newuser",form).then((res)=>{
console.log(res);
}).catch((err)=>{
  console.log(err);
})

}

  return (
    <>
    <div className='login'>
      <div className='container mt-5 child'>
        <div className='row'>
            <div className='col-md-7'>
                <div className='card'>
                    <div className='text-center mt-3'>
                    <h3>New User</h3>
                    </div>
                    <div className='card-body'>
                     <form>
                         <label>Name</label>
                         <input type="text" name="name" onChange={getData} value={User.Name} className='form-control'></input>
                         <label className='mt-2'>Email</label>
                         <input type="email" name="email" onChange={getData} value={User.Email} className='form-control'></input>
                         <label>User Name</label>
                         <input type="text" name="uname" onChange={getData} value={User.UserName} className='form-control'></input>
                         <label className='mt-2'>Password</label>
                         <input type="password" name="pass" onChange={getData} value={User.Password} className='form-control'></input>
                         <label className='mt-2'>Profile Image</label>
                         <input type="file" name="pic" onChange={(ev)=>{setPic(ev.target.files[0])}} className='form-control'></input>
                         <div className='text-center mt-3'>
                             <a href='/login'>LOGIN</a>
                         </div>
                         <button onClick={sub} className='form-control btn btn-success mt-4'>LOGIN</button>
                     </form>
                    </div>
                </div>
            </div>
        </div>
      </div>
      </div>
    </>
  )
}

export default NewUser
