import React from 'react'
import $ from 'jquery'
import LeftSide from './LeftSide'
import { useEffect,useState } from 'react'
import Navbar from './Navbar'
const Playlist = () => {
    const [isvis, setisvis] = useState(false);
    useEffect(() => {
      const md = sessionStorage.getItem('theme')
      document.body.className = md
    }, [])
  return (
    <>
       <div className='container-fluid'>
        <div className='row'>
          <div className='mt-3 bar'>
            <i className='fa fa-bars' id="barr" onClick={()=>{
              if(isvis)
              {
                $('.leftnav').css("display","none")
                setisvis(false);
              }
              else{
                $('.leftnav').css("display","block")
                setisvis(true);
              }

            }}></i>
          </div>
          <LeftSide></LeftSide>          
          <div className='col-md-10 rightnav'>
    <Navbar></Navbar>
    <div className='container-fluid'>
    <div className='container playbox  mt-5 shadow-lg p-3 mb-5 rounded'>
        <div className='row'>
          <div className='col-md-4 playleft'>
            <div className='mt-5 text-center'>
              <i className='fa fa-picture-o listpic'></i>
              <input type='file'></input> 
            </div>
          </div>
          <div className='col-md-8 playright'>
            <div className='mt-5'>
              <div className='row'>
              <h1>NEW PLAYLIST</h1>
              </div>              
            </div>
          </div>
        </div>
      </div>
        <div className='mt-3 my-3 row row-cols-1 row-cols-md-5 g-4'>

        {/* <audio id="sog" onEnded={() => {
            $('.playicon i').removeClass("fa fa-pause")
            $('.playicon i').addClass("fa fa-play")
        }}></audio> */}
        </div>
        </div>
        </div>
        </div>
      </div>
    </>
  )
}

export default Playlist
